const Counter = () => {
  const count = useSelector((state: RootState) => state.counter.value)
  const dispatch = useDispatch()

  return (
    <div>
      <div>
        <button
          aria-label="Increment value"
          onClick={() => dispatch(increment())}
        >
          Increment
        </button>
        <span>{count}</span>
        <button
          aria-label="Decrement value"
          onClick={() => dispatch(decrement())}
        >
          Decrement
        </button>
      </div>
    </div>
  )
}




interface IState {
  isFlipped: boolean
}
const FlippingCard = () => {
  let init: IState = {
    isFlipped: false
  }
  let [state, setState] = useState(init);
  return (
    <ReactCardFlip isFlipped={state.isFlipped} flipDirection="horizontal">
        <p>
          This is the front of the card.
          <button onClick={(e) => {e.preventDefault(); state.isFlipped = true; setState(state);}}>Click to flip</button>
        </p>
        <p>
          This is the back of the card.
          <button onClick={(e) => {e.preventDefault(); state.isFlipped = false; setState(state);}}>Click to flip</button>
        </p>
    </ReactCardFlip>
  )
}


