import React /*, { Component, useState, useEffect}*/ from 'react';
import {Select} from 'grommet';
import { addProductIndex, counterSlice } from '../../store/productListSlice';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../../store';
// import { fetchProductList } from '../../store/productListSlice';


// const handleClick = ({currentTarget}) => {
// }

export const SearchBar = (props: {options: string[]}) => {
		const count1 = useSelector((state: RootState) => state.counter.products)
	  const [value, setValue] = React.useState();
	  const dispatch = useDispatch();
	  return (
		<div onClick={(e) => {dispatch(addProductIndex(props.options.indexOf(e.currentTarget.id))); console.log()}}>
		<Select

		  placeholder="Choose your fighter"
		  options={props.options}
		//   options={['ff', 'ss']}

		  value={value}
		  onChange={({ option }) => setValue(option)}
		  onClick={(e) => dispatch(addProductIndex(props.options.indexOf(e.currentTarget.value)))}
		/>
		<span>{count1}</span>
		</div>
	  );
};
