import {
	createSlice,
	PayloadAction
} from '@reduxjs/toolkit';
const { sentence } = require('txtgen/dist/cjs/txtgen.js');

export interface SingleProductState {
	name: string;
	age: number;
	phrase: string;
	skills?: string;
	// photo: ImageData
}

const initialState: SingleProductState = {
	name: '',
	age: Math.ceil(Math.random() * 100),
	phrase: sentence()
}

export const singleProductSlice = createSlice({
	name: 'products',
	initialState,
	reducers: {
		addName: (state, action: PayloadAction<string>) => {
			state.name = action.payload;
		},
		editAge: (state, action: PayloadAction<number>) => {
			state.age = action.payload;
		},
		editSkills: (state, action: PayloadAction<string>) => {
			state.skills = action.payload;
		},
		editPhrase: (state, action: PayloadAction<string>) => {
			state.skills = action.payload;
		}
	}
});

export const {addName, editAge, editSkills, editPhrase} = singleProductSlice.actions;

export default singleProductSlice.reducer;
