import { Box, Card, CardBody, TextInput } from "grommet";
import React, { useState } from "react";
import ReactCardFlip from 'react-card-flip';
import { RootState } from "../../store";
import { useDispatch, useSelector } from 'react-redux';
import {removeProduct} from '../../store/Slices/productsSlice';
import {editSkills} from '../../store/Slices/singleProductSlice';
// import {addName, editAge, editSkills, editPhrase} from '../../store/Slices/singleProductSlice';
import {Edit as EDIT} from 'grommet-icons';
import  {Close} from 'grommet-icons';
import {Anchor, Form, Button} from 'grommet';
// const { sentence } = require('txtgen/dist/cjs/txtgen.js');


export const CardFlip = () => {
	const [isFlipped, setIsFlipped] = React.useState(false);
	const products = useSelector((state: RootState) => state.products.chosen_products);
	const age = useSelector((state: RootState) => state.product.age);
	const phrase = useSelector((state: RootState) => state.product.phrase);
	const skills = useSelector((state: RootState) => state.product.skills);
	const dispatch = useDispatch();
	const [text, setText] = useState('');
    const [isShown, setIsShown] = useState(false);

    const handleClick = () => {
      setIsShown(current => !current);
    };
	if (products.length === 0) return (<div/>)
	else
	return (
	<div className="row">
	{
		products.map(item => {(
			<span className="column" key = {item}>
				<ReactCardFlip isFlipped={isFlipped} flipDirection="horizontal">
					<Card className="card-hover color-6" onClick={() => {setIsFlipped((prev) => !prev); }}>
						<span onClick={(e) => {setIsFlipped((prev) => !prev); dispatch(removeProduct(item))}}>
							<Anchor icon={<Close />}/>
						</span>
						<CardBody>
							<h3>FIGHTER:</h3> <strong>{item}</strong>
							<p>AGE: {age}</p>
						</CardBody>
						<Box align='top'></Box>
					</Card>

					<Card className="card-hover color-6" onClick={() => setIsFlipped((prev) => !prev)}>
						<span onClick={(e) => {setIsFlipped((prev) => !prev); handleClick()}}>
							<Anchor icon={<EDIT />}/> 								
						</span>
						<CardBody>
							FIGHTER INFO
							<p><i>{phrase}</i></p>

						</CardBody>
						<CardBody onClick={() => {setIsFlipped((prev) => !prev);  }}>
							FEEDBACK:
							{isShown && (
								<Form onSubmit={(e) => {e.preventDefault(); dispatch(editSkills(text)); handleClick(); setText('')}}>
									<TextInput type="text" value={text} onChange={(e) => {setText(e.currentTarget.value)}} />
									<Button primary type="submit" color="white" label="save" icon={<EDIT />}/>
								</Form>
							)}
							{!isShown && (<p>"{skills}"</p>)}
						</CardBody>
						<Box>Здесь могла быть ваша реклама</Box>
					</Card> 

				</ReactCardFlip>
			</span>

			)}
		)
	}
	</div>
	);
  };
