import React, { useEffect, useState } from 'react';
import { Box, BoxExtendedProps, Grommet, Button, Select } from 'grommet';
import logo from "./icon_sber-01.png";
import './App.css';
import { rgb } from 'polished';
import axios from 'axios';
import UserData from './components/UserData'
import OnLoadingUserData from './components/LoadingPersonsData'

// const App: React

const theme = {
  global: {
    colors: {
      brand: rgb(0,128,0),
    },
    font: {
      family: "Copperplate",
      size: '20px',
      height: '20px',
      weight: 'bold',

    },
  },
};

const AppBarPre = (props: JSX.IntrinsicAttributes & BoxExtendedProps) => (
  <Box
    tag='header'
    direction='row'
    align='center'
    justify='between'
    background='#40C040'
    pad={{ left: 'medium', right: 'small', vertical: 'small' }}
    elevation='medium'
    style={{ zIndex: '1' }}
    // color='#FFFFFF'
    {...props}
  />
);

function SearchBar() {
  const DataLoading =  OnLoadingUserData(UserData);

  const [appState, setAppState] = useState(
    {
      loading: false,
      persons: null,
    }
  )
  useEffect(() => {
    setAppState({loading: true, persons: null});
    const apiUrl = 'http://www.filltext.com/?rows=32&id={number|1000}&firstName={firstName}&lastName={lastName}&email={email}&phone={phone|(xxx)xxx-xx-xx}&address={addressObject}&description={lorem|32}';
    axios.get(apiUrl).then((resp) => {
      const allPersons = resp.data;
      setAppState({
      loading: false,
      persons: allPersons
       });
    });
  }, [setAppState]);
  const [value, setValue] = React.useState();
  return (
    <Select

      placeholder="Выберите продукт"
      options={['small', 'medium', 'large']}
      value={value}
      onChange={({ option }) => setValue(option)}
    />
  );
}

const AppBar = (props: JSX.IntrinsicAttributes & BoxExtendedProps) => (
  <Box
    tag='header'
    direction='row'
    align='center'
    justify='between'
    background='white'
    pad={{ left: 'medium', right: 'small', vertical: 'small' }}
    elevation='medium'
    style={{ zIndex: '1' }}
    {...props}
  />
);

function App() {
  // const DataLoading =  OnLoadingUserData(UserData);

  // const [appState, setAppState] = useState(
  //   {
  //     loading: false,
  //     persons: null,
  //   }
  // )
  // useEffect(() => {
  //   setAppState({loading: true, persons: null});
  //   const apiUrl = 'http://www.filltext.com/?rows=32&id={number|1000}&firstName={firstName}&lastName={lastName}&email={email}&phone={phone|(xxx)xxx-xx-xx}&address={addressObject}&description={lorem|32}';
  //   axios.get(apiUrl).then((resp) => {
  //     const allPersons = resp.data;
  //     setAppState({
  //     loading: false,
  //     persons: allPersons
  //      });
  //   });
  // }, [setAppState]);

  return (
    <Grommet theme={theme}>

      <AppBarPre ></AppBarPre>
      <AppBar>
        <div><img src={logo} alt="logo" width="30px"/>
        <b className="p4">POWERED BY ALINA</b></div>
        <Button primary={false} title="В этой кнопке заключён главный принцип экзистенциализма Альбера Камю по версии Алины" label='tap' />
      </AppBar>
      <SearchBar >      <div className="app">
        <DataLoading isLoading={appState.loading} persons={appState.persons} />
      </div></SearchBar>
      {/* <div className="app">
        <DataLoading isLoading={appState.loading} persons={appState.persons} />
      </div> */}
    </Grommet>
  );
}

export default App;
