import { RootState } from "../../store";
import {useSelector } from 'react-redux';
import {CardFlip} from './CardFlip';
import {addChosenObjects} from '../../store/Slices/productsSlice'
const { sentence } = require('txtgen/dist/cjs/txtgen.js');

//!постоянная смена phrase/age при ререндере
interface IProductsInfo {
	name?: string;
	age?: number;
	phrase?: string;
	skills?: string;
};

export const CardsFlip = () => {
	const products = useSelector((state: RootState) => state.products.chosen_products);
	// let chosen_objects = useSelector((state: RootState) => state.products.chosen_objects);
	let chosen_objects: IProductsInfo[] = [];


		console.log(products.length);
		let i: number = -1;
		// while (++i < products.length) {
		// 	chosen_objects[i].name = products[i];
		// 	chosen_objects[i].age = Math.ceil(Math.random() * 100);
		// 	chosen_objects[i].phrase = sentence();
		// 	chosen_objects[i].skills = '';
		// }
		while (++i < products.length) {
			let obj: IProductsInfo = {};

			obj.name = products[i];
			obj.age = Math.ceil(Math.random() * 100);
			obj.phrase = sentence();
			obj.skills = '';
			// chosen_objects.push(obj);
			console.log(i)
		}
		console.log(chosen_objects);
		dispatch(addChosenObjects(chosen_objects));

	if (products.length === 0) return (<div/>)
	else
	return (
	<div className="row">
	{
		products.map(item => (
				<CardFlip item={item} key={item}></CardFlip>
			)
		)
	}
	</div>
	);
  };
