import React, { Component, useState, useEffect} from 'react';
import { Box, BoxExtendedProps, Grommet, Button, Select } from 'grommet';
import logo from "./icon_sber-01.png";
import './App.css';
import { rgb } from 'polished';
import axios from 'axios';
import { trackPromise, usePromiseTracker } from 'react-promise-tracker';


const area = 'persons';
const apiUrl = 'http://www.filltext.com/?rows=32&id={number|1000}&firstName={firstName}&lastName={lastName}&email={email}&phone={phone|(xxx)xxx-xx-xx}&address={addressObject}&description={lorem|32}';

const theme = {
  global: {
    colors: {
      brand: rgb(0,128,0),
    },
    font: {
      family: "Copperplate",
      size: '20px',
      height: '20px',
      weight: 'bold',

    },
  },
};

const AppBarPre = (props: JSX.IntrinsicAttributes & BoxExtendedProps) => (
  <Box
    tag='header'
    direction='row'
    align='center'
    justify='between'
    background='#40C040'
    pad={{ left: 'medium', right: 'small', vertical: 'small' }}
    elevation='medium'
    style={{ zIndex: '1' }}
    // color='#FFFFFF'
    {...props}
  />
);

function SearchBar() {

  const [value, setValue] = React.useState();
  return (
    <Select

      placeholder="Выберите продукт"
      options={['small', 'medium', 'large']}
      value={value}
      onChange={({ option }) => setValue(option)}
    />
  );
}

const AppBar = (props: JSX.IntrinsicAttributes & BoxExtendedProps) => (
  <Box
    tag='header'
    direction='row'
    align='center'
    justify='between'
    background='white'
    pad={{ left: 'medium', right: 'small', vertical: 'small' }}
    elevation='medium'
    style={{ zIndex: '1' }}
    {...props}
  />
);

function App() {
  const { promiseInProgress } = usePromiseTracker({ area });
  const [ persons, setPersons ] = useState(null);
  const [appState, setAppState] = useState(
    {
      loading: false,
      persons: null,
    }
  )

  useEffect(() => {
    trackPromise(axios.get(apiUrl), area).then(({ data }) => {
      setPersons(data);
    });
  }, [setAppState]);
  return (
    <Grommet theme={theme}>

      <AppBarPre ></AppBarPre>
      <AppBar>
        <div><img src={logo} alt="logo" width="30px"/>
        <b className="p4">POWERED BY ALINA</b></div>
        <Button primary={false} title="В этой кнопке заключён главный принцип экзистенциализма Альбера Камю по версии Алины" label='tap' />
      </AppBar>
      <SearchBar></SearchBar>
      <div className="app">
          {promiseInProgress ? <div>Подождите, данные загружаются!</div> : <Component data={persons} /> }
      </div>
    </Grommet>
  );
}

export default App;
