            		// console.log(str);
				index = props.options.indexOf(str);

				if (index >= 0 && index < props.options.length) {
            		dispatch(addProductIndex(props.options.indexOf(str)));
					console.log(index);
				}
				console.log(count1);
          }
          }
          >
          <Select

            placeholder="Choose your fighter"
            options={props.options}
