import { /*createSlice, nanoid,*/ createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export const fetchProductList = createAsyncThunk('productList/fetchProductList', async () => {
	const response = await axios.get(
	  'https://petstore.swagger.io/v2/pet/findByStatus?status=available'
	);
	return response.data.productList;
  });

  console.log(fetchProductList.toString());
  export const productListSlice = createSlice({
	name: 'productList',
	initialState: {
	  todoList: [],
	  status: 'idle',
	  error: null,
	},
	reducers: {
	  addProduct: {
		reducer: (state, action) => {
		  state.todoList.push(...action.payload);
		},
		prepare(value) {
		  return {
			payload: {
			  key: nanoid(),
			  value: value,
			},
		  };
		},
	  },
	},
	extraReducers: {
	  [fetchProductList.pending]: (state, action) => {
		state.status = 'loading';
	  },
	  [fetchProductList.fulfilled]: (state, action) => {
		state.status = 'succeeded';
		state.todoList.push(...action.payload);
	  },
	  [fetchProductList.rejected]: (state, action) => {
		state.status = 'failed';
		state.error = action.error.message;
	  },
	},
  });
