import { Box, Card, CardBody, TextInput } from "grommet";
import React, { useState } from "react";
import ReactCardFlip from 'react-card-flip';
import { RootState } from "../../store";
// import { CounterState } from "../../store/productListSlice";
import { useDispatch, useSelector } from 'react-redux';
import {removeProduct} from '../../store/Slices/productsSlice';
import {addName, editAge, editSkills, editPhrase} from '../../store/Slices/singleProductSlice';
import {Edit as EDIT} from 'grommet-icons';
import  {Close} from 'grommet-icons';
import {Anchor, Form, Button} from 'grommet';
const { sentence } = require('txtgen/dist/cjs/txtgen.js');
const { EditText } = require('react-edit-text/dist/index.js');
// import 'react-edit-text/dist/index.css';


// export const CardFlip = (props: CounterState) => {
export const CardFlip = () => {
	const [isFlipped, setIsFlipped] = React.useState(false);
	const products = useSelector((state: RootState) => state.products.chosen_products);
	const age = useSelector((state: RootState) => state.product.age);
	const phrase = useSelector((state: RootState) => state.product.phrase);
	const skills = useSelector((state: RootState) => state.product.skills);
	const dispatch = useDispatch();
	const [skill, setSkill] = useState('');
	const [words, setWords] = useState('');
    const [isShownSkills, setIsShownSkills] = useState(false);
    const [isShownPhrase, setIsShownPhrase] = useState(false);

    const handleClickSkills = () => {
      setIsShownSkills(current => !current);
    };
	const handleClickPhrase = () => {
		setIsShownSkills(current => !current);
	  };
	if (products.length === 0) return (<div/>)
	else
	return (
	<div className="row">
	{
		products.map(item => (
				<span className="column" key = {item}>
				<ReactCardFlip isFlipped={isFlipped} flipDirection="horizontal">
					<div onClick={() => {setIsFlipped((prev) => !prev); }}>
						<Card className="card-hover color-6">
						<span onClick={(e) => {setIsFlipped((prev) => !prev); dispatch(removeProduct(item))}}><Anchor icon={<Close />}/> </span>
							<CardBody>
								<h3>FIGHTER:</h3> <strong>{item}</strong>
								<p>AGE: {age}</p>
							</CardBody>
							<Box align='top'></Box>
						</Card>
					</div>
					<div  onClick={() => setIsFlipped((prev) => !prev)}>
						<Card className="card-hover color-6">
						<div  onClick={() => {setIsFlipped((prev) => !prev);  }}><span onClick={
							(e) => {
								// setIsFlipped((prev) => !prev);    
								handleClick();   
								console.log("NY EMAE");

								}}>
								<Anchor icon={<EDIT />}/> 								
							</span>

						</div>

								<>
									FIGHTER INFO
									<p><i>{phrase}</i></p>
									<CardBody onClick={() => {setIsFlipped((prev) => !prev);  }}>
									{isShownPhrase && (
									<Form onSubmit={(e) => {e.preventDefault(); dispatch(editPhrase(words)); handleClick(); setWords('')}}>
										<TextInput type="text" value={words} onChange={(e) => {setWords(e.currentTarget.value)}} />
										<Button primary type="submit" color="white" label="save" icon={<EDIT />}/>
									</Form>
									)}
									{!isShownPhrase && (<p>{words}</p>)}
									</CardBody>
								</>
								<>
								SKILLS:
									<CardBody onClick={() => {setIsFlipped((prev) => !prev);  }}>
										{isShownSkills && (
										<Form onSubmit={(e) => {e.preventDefault(); dispatch(editSkills(skill)); handleClick(); setSkill('')}}>
											<TextInput type="text" value={skill} onChange={(e) => {setSkill(e.currentTarget.value)}} />
											<Button primary type="submit" color="white" label="save" icon={<EDIT />}/>
										</Form>
										)}
										{!isShownSkills && (<p>{skills}</p>)}
									</CardBody>
								</>
							<Box>Здесь могла быть ваша реклама</Box>
						</Card>     
					</div>
				</ReactCardFlip>
				</span>

			)
		)

	}

	 </div>
	);

  };
