import React from 'react';
import { Box, BoxExtendedProps, Grommet, Button, Select } from 'grommet';
import logo from "./icon_sber-01.png";
import './App.css';
import { rgb } from 'polished';

// const App: React

const theme = {
  global: {
    colors: {
      brand: rgb(0,128,0),
    },
    font: {
      family: "Copperplate",
      size: '20px',
      height: '20px',
      weight: 'bold',

    },
  },
};

const AppBarPre = (props: JSX.IntrinsicAttributes & BoxExtendedProps) => (
  <Box
    tag='header'
    direction='row'
    align='center'
    justify='between'
    background='#40C040'
    pad={{ left: 'medium', right: 'small', vertical: 'small' }}
    elevation='medium'
    style={{ zIndex: '1' }}
    // color='#FFFFFF'
    {...props}
  />
);

function SearchBar() {

  const [value, setValue] = React.useState();
  return (
    <Select

      placeholder="Выберите продукт"
      options={['small', 'medium', 'large']}
      value={value}
      onChange={({ option }) => setValue(option)}
    />
  );
}

const AppBar = (props: JSX.IntrinsicAttributes & BoxExtendedProps) => (
  <Box
    tag='header'
    direction='row'
    align='center'
    justify='between'
    background='white'
    pad={{ left: 'medium', right: 'small', vertical: 'small' }}
    elevation='medium'
    style={{ zIndex: '1' }}
    {...props}
  />
);

function App() {
  return (
    <Grommet theme={theme}>
      {/* <header className="App-header">
        <p>
          Edit <code>src/App.tsx</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header> */}
      {/* title={<img src=""/>} */}
      <AppBarPre ></AppBarPre>
      <AppBar>
        <div><img src={logo} alt="logo" width="30px"/>
        <b className="p4">POWERED BY ALINA</b></div>
        <Button primary={false} title="В этой кнопке заключён главный принцип экзистенциализма Альбера Камю по версии Алины" label='tap' />
      </AppBar>
      <SearchBar className></SearchBar>
    </Grommet>
  );
}

export default App;
